import classes from './MealItemForm.module.css';
import Input from '../../UI/Input';
import { useState } from 'react';



const MealItemForm = (props) =>{

    const [state, changeState] =  useState("");
    const [cases, changeCases] = useState(0);
    const [samples, changeSamples] = useState(0);
    const [posive, changePositive] = useState(0);
    const [negaive, changeNegative] = useState(0);
    const [death, changeDeath] = useState(0);
    const [discharge, changeDischarge] = useState(0);


    const addItemsServer = ()=>{
        console.log(state+cases+samples+posive+negaive+death)
    }


    const changerState = (e)=>{
        console.log(e.target.value);
    }

    return(

        <form className={classes.form}>

            <Input label="Enter state"

            input={{

                id:"state",
                type:"text",
                label:"Enter state",
            }}

            onChange = {changerState}
            />
            <Input label="Cases" 
             onChange={e => changeCases(e.target.value)}
            input={{
                id:"amount",
                type: "number",
                min:'1',
                max:'100',
                step:'1',
                defaultValue:'0',
            }}/>
             <Input label="Number of samples" 
             onChange={e => changeSamples(e.target.value)}

             input={{
                       id:"samples",
                       type: "number",
                       min:'1',
                       max:'100',
                       step:'1',
                      defaultValue:'0',
            }}/>
                        <Input label="No. of positive case" 
                        onChange={e => changePositive(e.target.value)

                        }
                        input={{
                         id:"positive",
                       type: "number",
                       min:'1',
                       max:'100',
                       step:'1',
                      defaultValue:'0',
            }}/>
                        <Input label="No. of negative case" 

                        onChange={e => changeNegative(e.target.value)}
                        input={{
                    id:"negative",
                    type: "number",
                    min:'1',
                    max:'100',
                    step:'1',
                   defaultValue:'0',
            }}/>
                        <Input label="Discharge" 
                        onChange={e => changeDischarge(e.target.value)}
                        input={{
                    id:"discharge",
                    type:"number",
                    label:"Discharge"
            }}/>
                        <Input label="death" 
                         onChange={e => changeDeath(e.target.value)}
                        input={{
                id:"death",
                type:"number",
                label:"death"
            }}/>
            <button onClick={addItemsServer}>Add</button>
        </form>
    )}

export default MealItemForm;
